export function MakeTodoActions(): TodoActionsInterface {
  return new TodoActions();
}

export interface TodoActionsInterface {
  addTodo(text: string): any;
  toggleTodo(id: number): any;
}

export const Actions = {
  ADD_TODO: 'ADD_TODO',
  TOOGLE_TODO: 'TOOGLE_TODO'
}

class TodoActions implements TodoActionsInterface {
  constructor() { }

  public addTodo(text: string) {
    return {
      type: Actions.ADD_TODO,
      id: TodoActions.nextTodoId++,
      text
    };
  };

  public toggleTodo(id: number) {
    return {
      type: Actions.TOOGLE_TODO,
      id
    }
  }

  static nextTodoId: number = 0;
}