export function MakeTodoActions(action?: Actions): TodoActionsInterface {
  return new TodoActions(action || new Actions());
}

export interface TodoActionsInterface {
  addTodo(text: string): any;
}

class Actions {
  ADD_TODO: 'ADD_TODO';
}

class TodoActions implements TodoActionsInterface {
  private static nextTodoId: number = 0;
  private action: Actions;

  constructor(action: Actions) {
    this.action = action;
  }

  public addTodo(text: string) {
    return {
      type: this.action.ADD_TODO,
      id: TodoActions.nextTodoId++,
      text
    };
  };
}