import * as React from "react";

export interface TodoProps {
    onClick: any;
    completed: boolean;
    text: string;
};

export const Todo = (props: TodoProps) => (
    <a  onClick={props.onClick}
        style={{
            textDecoration: props.completed ? 'line-through' : 'none'
        }}
        className="collection-item"
    >
        {props.text}
    </a>
)