import * as React from "react";
import { Todo } from 'components/Todo';

export interface TodoListProps {
    todos?: [{id: number, completed: string, text: string}];
    onTodoClick?: any;    
};

export const TodoList = (props: TodoListProps) => (
    <ul>
        {props.todos.map(todo => <Todo key={todo.id} {...todo} onClick={props.onTodoClick(todo.id)} />)}
    </ul>
)