import * as React from 'react'
import { connect } from 'react-redux'
import { MakeTodoActions } from 'actions/TodoActions';

export interface AddTodoInterface {
    dispatch?: any;
};


let AddTodo = (AddTodoProps: AddTodoInterface) => {
    let input: any;

    return (
        <div>
            <form onSubmit={e => {
                e.preventDefault()
                if (!input.value.trim()) {
                    return
                }
                AddTodoProps.dispatch(MakeTodoActions().addTodo(input.value))
                input.value = ''
            }}>
                <input ref={node => {
                    input = node
                }} placeholder="Working hard, play hard"/>
                <button type="submit" className="btn waves-effect waves-light">
                    Add Todo
                </button>
            </form>
        </div>
    )
}

AddTodo = connect()(AddTodo)
export default AddTodo;