import { Actions } from 'actions/TodoActions';
import { TodoModel } from 'models/TodoModel';
import todo from 'reducers/TodoReducer';

const initialState: TodoModel[] = [
    {
        text: 'Use Redux React TypeScript With Rodgers',
        completed: false,
        id: 0
    }
]

export default function todos(state = initialState, action: any) {
    switch (action.type) {
        case Actions.ADD_TODO:
            console.log("Add Action", action, state);
            return [
                {
                    id: state.reduce((maxId, todo) => Math.max(todo.id, maxId), -1) + 1,
                    completed: false,
                    text: action.text
                },
                ...state
            ];
        case Actions.TOOGLE_TODO:
            return state.map(t =>
                todo(t, action)
            )
        default:
            return state
    }
}
