import { Actions } from 'actions/TodoActions';
import { TodoModel } from 'models/TodoModel';

export default function todo(state: any, action: any) {
    switch (action.type) {
        case Actions.TOOGLE_TODO:
            if(state.id != action.id){
                return state;
            };
            return {
                ...state,
                completed: !state.completed
            }
        default:
            return state
    }
}
